                netArtistInfo.setCoverImgUrl(jsonObject.optString(ConfigConstants.NET_ARTIST_COVER_IMG_URL));
                netArtistInfo.setCoverImgThumbUrl(jsonObject.optString(ConfigConstants.NET_ARTIST_COVER_IMG_THUMB_URL));
