        netMvGoButton.setToolTipText(GO_TIP);
        netMvNextPageButton.setToolTipText(NEXT_PAGE_TIP);
        // 回到关键词面板起始不可用
        netMvBackwardButton.setEnabled(false);
        // 清除输入按钮初始不可见
